import"gl-matrix";let gl=null;function initWebGLContext(canvasName){const canvas=document.getElementById(canvasName);if(canvas===null){console.log(`Failed to get a canvas element with the name "${canvasName}"`);return false}gl=canvas.getContext("webgl",{alpha:false,premultipliedAlpha:false});return true}const startButton=document.getElementById("startButton");startButton.onclick=onStartButtonClick;function onStartButtonClick(){console.log("Start game")}function draw(){gl.clear(gl.COLOR_BUFFER_BIT)}function init(){if(!initWebGLContext("renderCanvas"))return;gl.clearColor(.2,.3,.1,1);startButton.hidden=false;const startButtonHalfWidth=startButton.clientWidth/2;const startButtonHalfHeight=startButton.clientHeight/2;const startButtonPosX=gl.canvas.clientWidth/2-startButtonHalfWidth;startButton.style.left=`${startButtonPosX}px`;const startButtonPosY=gl.canvas.clientHeight/2-startButtonHalfHeight;startButton.style.top=`${startButtonPosY}px`;draw()}init();